import{_ as e,o as a,c as s,e as i}from"./app.3fa41763.js";const r={},d=i('<h1 id="\u110C\u1161\u110C\u116E-\u1106\u116E\u11AE\u1102\u1173\u11AB-\u110C\u1175\u11AF\u1106\u116E\u11AB" tabindex="-1"><a class="header-anchor" href="#\u110C\u1161\u110C\u116E-\u1106\u116E\u11AE\u1102\u1173\u11AB-\u110C\u1175\u11AF\u1106\u116E\u11AB" aria-hidden="true">#</a> \uC790\uC8FC \uBB3B\uB294 \uC9C8\uBB38</h1><p>\uC5EC\uAE30\uC5D0\uC11C\uB294 Misskey\uB97C \uC774\uC6A9\uD558\uBA74\uC11C \uC790\uC8FC \uBB3B\uB294 \uC9C8\uBB38\uC5D0 \uB300\uD574 \uB2E4\uB8E8\uACE0 \uC788\uC2B5\uB2C8\uB2E4. <br> Misskey \uD504\uB85C\uC81D\uD2B8 \uC790\uCCB4\uC5D0 \uB300\uD55C \uC790\uC8FC \uBB3B\uB294 \uC9C8\uBB38\uC740 <a href="./misskey">\uC5EC\uAE30\uC5D0\uC11C</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="android-ios\u110B\u116D\u11BC-\u110B\u1165\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB\u110B\u1175-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#android-ios\u110B\u116D\u11BC-\u110B\u1165\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB\u110B\u1175-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" aria-hidden="true">#</a> Android/iOS\uC6A9 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC788\uB098\uC694?</h2><p>Misskey \uD504\uB85C\uC81D\uD2B8\uC5D0\uC11C \uD2B9\uC815 OS\uB97C \uB300\uC0C1\uC73C\uB85C \uD558\uC5EC \uAC1C\uBC1C\uD558\uACE0 \uC788\uB294 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC740 \uC5C6\uC9C0\uB9CC, \uC81C 3\uC790\uC5D0 \uC758\uD574 \uAC1C\uBC1C\uB41C \uC11C\uB4DC\uD30C\uD2F0 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC874\uC7AC\uD569\uB2C8\uB2E4. \uC790\uC138\uD55C \uC0AC\uD56D\uC740 <a href="./apps">\uC5EC\uAE30</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uB2E8, \uC11C\uB4DC\uD30C\uD2F0 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC740 Misskey\uC758 \uCD5C\uC2E0 \uAE30\uB2A5\uC744 \uC774\uC6A9\uD560 \uC218 \uC5C6\uB294 \uACBD\uC6B0\uAC00 \uD544\uC5F0\uC801\uC73C\uB85C \uBC1C\uC0DD\uD558\uAE30 \uB54C\uBB38\uC5D0, \uD544\uC694\uD55C \uACBD\uC6B0\uAC00 \uC544\uB2C8\uB77C\uBA74 \uACF5\uC2DD Misskey Web \uD074\uB77C\uC774\uC5B8\uD2B8\uB97C \uC774\uC6A9\uD558\uC2DC\uB294 \uAC83\uC744 \uCD94\uCC9C\uB4DC\uB9BD\uB2C8\uB2E4. \uB610\uD55C, \uACF5\uC2DD Web \uD074\uB77C\uC774\uC5B8\uD2B8\uB294 PWA\uB97C \uC9C0\uC6D0\uD558\uACE0 \uC788\uC5B4 \uC77C\uBC18\uC801\uC778 \uC571\uCC98\uB7FC \uC0AC\uC6A9\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC790\uC138\uD55C \uC0AC\uD56D\uC740 <a href="./todo">\uC5EC\uAE30</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="mastodon\u110B\u116D\u11BC-\u110B\u1165\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB\u110B\u1173\u1105\u1169-\u1105\u1169\u1100\u1173\u110B\u1175\u11AB\u1112\u1161\u11AF-\u1109\u116E-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#mastodon\u110B\u116D\u11BC-\u110B\u1165\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB\u110B\u1173\u1105\u1169-\u1105\u1169\u1100\u1173\u110B\u1175\u11AB\u1112\u1161\u11AF-\u1109\u116E-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" aria-hidden="true">#</a> Mastodon\uC6A9 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC73C\uB85C \uB85C\uADF8\uC778\uD560 \uC218 \uC788\uB098\uC694?</h2><p>Misskey\uC740 Mastodon\uC758 API\uC640 \uD638\uD658\uB418\uC9C0 \uC54A\uC544, \uC77C\uBD80\uB97C \uC81C\uC678\uD558\uBA74 Mastodon\uC6A9 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC774\uB098 \uC6F9 \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C\uB294 Misskey\uB97C \uC774\uC6A9\uD558\uC2E4 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. Misskey \uACF5\uC2DD \uC6F9 \uD074\uB77C\uC774\uC5B8\uD2B8\uB97C \uC774\uC6A9\uD574 \uC8FC\uC2ED\uC2DC\uC624.</p><h2 id="misskey-io\u1102\u1173\u11AB-misskey-\u1100\u1169\u11BC\u1109\u1175\u11A8-\u110B\u1175\u11AB\u1109\u1173\u1110\u1165\u11AB\u1109\u1173\u110B\u1175\u11AB\u1100\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#misskey-io\u1102\u1173\u11AB-misskey-\u1100\u1169\u11BC\u1109\u1175\u11A8-\u110B\u1175\u11AB\u1109\u1173\u1110\u1165\u11AB\u1109\u1173\u110B\u1175\u11AB\u1100\u1161\u110B\u116D" aria-hidden="true">#</a> misskey.io\uB294 Misskey \uACF5\uC2DD \uC778\uC2A4\uD134\uC2A4\uC778\uAC00\uC694?</h2><p>misskey.io\uB294 Misskey \uACF5\uC2DD \uC778\uC2A4\uD134\uC2A4\uAC00 \uC544\uB2C8\uBA70, Misskey \uD504\uB85C\uC81D\uD2B8\uC640 \uBCC4\uAC1C\uB85C \uC6B4\uC601\uB418\uACE0 \uC788\uC2B5\uB2C8\uB2E4. misskey.io\uB294 \uD2B9\uC815\uD55C \uC8FC\uC81C \uC5C6\uC774 \uB2E4\uC591\uD55C \uC774\uC57C\uAE30\uAC00 \uC624\uAC00\uBA70, \uAC00\uBCCD\uAC8C \uC774\uC6A9\uD560 \uC218 \uC788\uB294 Misskey \uCD5C\uB300 \uADDC\uBAA8\uC758 \uC778\uC2A4\uD134\uC2A4\uC785\uB2C8\uB2E4.</p><h2 id="misskey-\u1105\u1161\u1102\u1173\u11AB-\u110B\u1175\u1105\u1173\u11B7\u110B\u1174-\u110B\u1172\u1105\u1162\u1102\u1173\u11AB-\u1106\u116E\u110B\u1165\u11BA\u110B\u1175\u11AB\u1100\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#misskey-\u1105\u1161\u1102\u1173\u11AB-\u110B\u1175\u1105\u1173\u11B7\u110B\u1174-\u110B\u1172\u1105\u1162\u1102\u1173\u11AB-\u1106\u116E\u110B\u1165\u11BA\u110B\u1175\u11AB\u1100\u1161\u110B\u116D" aria-hidden="true">#</a> &#39;Misskey&#39;\uB77C\uB294 \uC774\uB984\uC758 \uC720\uB798\uB294 \uBB34\uC5C7\uC778\uAC00\uC694?</h2><p>syuilo\uAC00 \uD504\uB85C\uC81D\uD2B8 \uC774\uB984\uC744 \uAD6C\uC0C1\uD560 \uB54C, \uC6B0\uC5F0\uD788 \uB4E3\uACE0 \uC788\uB358 May&#39;n\uC758 \uB178\uB798 &#39;Brain Diver&#39;\uC758 \uAC00\uC0AC\uC5D0\uC11C \uB530 \uC628 \uAC83\uC785\uB2C8\uB2E4.</p><h2 id="\u1103\u1161\u1105\u1173\u11AB-\u1109\u1165\u1107\u1165\u110B\u1174-\u110B\u1172\u110C\u1165\u1105\u1173\u11AF-\u1111\u1161\u11AF\u1105\u1169\u110B\u116E\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u1105\u1173\u11AB-\u1109\u1165\u1107\u1165\u110B\u1174-\u110B\u1172\u110C\u1165\u1105\u1173\u11AF-\u1111\u1161\u11AF\u1105\u1169\u110B\u116E\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" aria-hidden="true">#</a> \uB2E4\uB978 \uC11C\uBC84\uC758 \uC720\uC800\uB97C \uD314\uB85C\uC6B0\uD558\uACE0 \uC2F6\uC5B4\uC694!</h2><p>\uBA54\uB274\uC5D0\uC11C \uAC80\uC0C9\uC744 \uC120\uD0DD\uD558\uC5EC, \uD314\uB85C\uC6B0\uD558\uB824\uB294 \uC720\uC800\uC758 \uC8FC\uC18C\uB97C \uC785\uB825\uD558\uC5EC \uB2E4\uB978 \uC11C\uBC84\uC758 \uC720\uC800\uB97C \uCC3E\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC720\uC800\uC758 \uC8FC\uC18C\uB294 \uC774\uBA54\uC77C \uC8FC\uC18C\uC640 \uBE44\uC2B7\uD55C \uD615\uD0DC\uB85C, <strong>@\uB85C \uC2DC\uC791\uD558\uB294 \uC0AC\uC6A9\uC790\uBA85</strong>\uACFC <strong>\uC720\uC800\uAC00 \uC18D\uD55C \uC11C\uBC84\uC758 \uD638\uC2A4\uD2B8\uBA85</strong>\uC73C\uB85C \uC774\uB8E8\uC5B4\uC838 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uC608\uB97C \uB4E4\uC5B4, <code>misskey.io</code>\uC5D0 \uC788\uB294 <code>@syuilo</code>\uB77C\uB294 \uC720\uC800\uB97C \uCC3E\uC744 \uB54C\uC5D0\uB294, <code>@syuilo@misskey.io</code>\uC640 \uAC19\uC774 \uC785\uB825\uD569\uB2C8\uB2E4.</p><p>Misskey\uBFD0\uB9CC \uC544\uB2C8\uB77C, Mastodon\uC774\uB098 Pleroma\uB97C \uBE44\uB86F\uD55C \uB9CE\uC740 \uBD84\uC0B0\uD615 \uD50C\uB7AB\uD3FC\uC5D0\uC11C \uC774\uB7EC\uD55C \uC8FC\uC18C \uD615\uC2DD\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB2E4\uB978 \uC11C\uBC84\uC758 \uC720\uC800\uB97C \uCC3E\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="renote-\u1105\u1173\u11AF-\u110E\u1171\u1109\u1169\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#renote-\u1105\u1173\u11AF-\u110E\u1171\u1109\u1169\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" aria-hidden="true">#</a> Renote \uB97C \uCDE8\uC18C\uD558\uACE0 \uC2F6\uC5B4\uC694!</h2><p>Renote \uC2DC\uAC01 \uD45C\uC2DC \uC624\uB978\uCABD\uC5D0 \uC788\uB294 3\uC810 \uBA54\uB274(&quot;...&quot;)\uB97C \uB20C\uB7EC, &#39;Renote \uCDE8\uC18C&#39;\uB97C \uB20C\uB7EC \uC8FC\uC138\uC694.<br> Renote\uC5D0 \uB300\uD55C \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="../features/note">\uC5EC\uAE30</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="url\u110B\u1174-\u1106\u1175\u1105\u1175\u1107\u1169\u1100\u1175\u1105\u1173\u11AF-\u1109\u116E\u11B7\u1100\u1175\u11AF-\u1109\u116E-\u110B\u1165\u11B9\u1102\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#url\u110B\u1174-\u1106\u1175\u1105\u1175\u1107\u1169\u1100\u1175\u1105\u1173\u11AF-\u1109\u116E\u11B7\u1100\u1175\u11AF-\u1109\u116E-\u110B\u1165\u11B9\u1102\u1161\u110B\u116D" aria-hidden="true">#</a> URL\uC758 \uBBF8\uB9AC\uBCF4\uAE30\uB97C \uC228\uAE38 \uC218 \uC5C6\uB098\uC694?</h2><p>MFM(Markup language For Misskey)\uB77C\uB294 Misskey\uB9CC\uC758 \uB3C5\uC790\uC801\uC778 \uB9C8\uD06C\uC5C5 \uC5B8\uC5B4\uB97C \uC774\uC6A9\uD558\uC5EC, \uBCF4\uC774\uACE0 \uC2F6\uC9C0 \uC54A\uC740 URL\uC744 \uC228\uAE38 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 \uAC01 \uC778\uC2A4\uD134\uC2A4\uC758 MFM \uB3C4\uC6C0\uB9D0\uC744 \uCC38\uC870\uD574 \uC8FC\uC138\uC694. \uC8FC\uC18C\uCC3D\uC5D0 \uB2E4\uC74C\uACFC \uAC19\uC740 \uD615\uC2DD\uC73C\uB85C \uC785\uB825\uD558\uC2DC\uBA74 \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4. <code>https://&lt;\uB098\uC758 \uC778\uC2A4\uD134\uC2A4 \uD638\uC2A4\uD2B8 \uBA85&gt;/mfm-cheat-sheet</code></p><h2 id="\u110F\u1165\u1109\u1173\u1110\u1165\u11B7-\u110B\u1175\u1106\u1169\u110C\u1175\u1105\u1173\u11AF-\u110E\u116E\u1100\u1161\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u110F\u1165\u1109\u1173\u1110\u1165\u11B7-\u110B\u1175\u1106\u1169\u110C\u1175\u1105\u1173\u11AF-\u110E\u116E\u1100\u1161\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" aria-hidden="true">#</a> \uCEE4\uC2A4\uD140 \uC774\uBAA8\uC9C0\uB97C \uCD94\uAC00\uD558\uACE0 \uC2F6\uC5B4\uC694!</h2><p>\uCEE4\uC2A4\uD140 \uC774\uBAA8\uC9C0\uC758 \uCD94\uAC00, \uD3B8\uC9D1, \uC0AD\uC81C\uB294 \uC790\uC2E0\uC774 \uC18D\uD55C \uC778\uC2A4\uD134\uC2A4\uC758 \uAD00\uB9AC\uC790\uB9CC \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uCEE4\uC2A4\uD140 \uC774\uBAA8\uC9C0\uC5D0 \uB300\uD55C \uC694\uCCAD\uC740 \uAC01 \uC778\uC2A4\uD134\uC2A4 \uAD00\uB9AC\uC790\uC5D0\uAC8C \uBB38\uC758\uD574 \uC8FC\uC2ED\uC2DC\uC624.</p><h2 id="\u1107\u1169\u11BA\u110B\u1173\u11AF-\u1100\u1162\u1107\u1161\u11AF\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1107\u1169\u11BA\u110B\u1173\u11AF-\u1100\u1162\u1107\u1161\u11AF\u1112\u1161\u1100\u1169-\u1109\u1175\u11C1\u110B\u1165\u110B\u116D" aria-hidden="true">#</a> \uBD07\uC744 \uAC1C\uBC1C\uD558\uACE0 \uC2F6\uC5B4\uC694!</h2><p>Misskey API\uB97C \uC774\uC6A9\uD558\uB294 \uBD07\uC744 \uAC1C\uBC1C\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="../advanced/develop-bot">\uC5EC\uAE30</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="\u1102\u1169\u1110\u1173-\u1107\u1165\u11AB\u110B\u1167\u11A8-\u1100\u1175\u1102\u1173\u11BC\u110B\u1173\u11AB-\u110B\u1165\u1104\u1165\u11AB-\u1109\u1165\u1107\u1175\u1109\u1173\u1105\u1173\u11AF-\u1112\u116A\u11AF\u110B\u116D\u11BC\u1112\u1161\u1100\u1169-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1102\u1169\u1110\u1173-\u1107\u1165\u11AB\u110B\u1167\u11A8-\u1100\u1175\u1102\u1173\u11BC\u110B\u1173\u11AB-\u110B\u1165\u1104\u1165\u11AB-\u1109\u1165\u1107\u1175\u1109\u1173\u1105\u1173\u11AF-\u1112\u116A\u11AF\u110B\u116D\u11BC\u1112\u1161\u1100\u1169-\u110B\u1175\u11BB\u1102\u1161\u110B\u116D" aria-hidden="true">#</a> \uB178\uD2B8 \uBC88\uC5ED \uAE30\uB2A5\uC740 \uC5B4\uB5A4 \uC11C\uBE44\uC2A4\uB97C \uD65C\uC6A9\uD558\uACE0 \uC788\uB098\uC694?</h2><p>Misskey \uAE30\uBCF8 \uBC88\uC5ED \uAE30\uB2A5\uC740 <a href="https://www.deepl.com/" target="_blank" rel="noopener noreferrer">DeepL\uC758 \uBC88\uC5ED \uC11C\uBE44\uC2A4</a>\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. (2022\uB144 9\uC6D4 \uAE30\uC900 \uD55C\uAD6D\uC5B4 \uBBF8\uC9C0\uC6D0)</p>',25),o=[d];function h(n,t){return a(),s("div",null,o)}var p=e(r,[["render",h],["__file","faq.html.vue"]]);export{p as default};
